INSERT INTO public.faqs (question,answer,order_index,status,category_id,"createdAt","updatedAt") VALUES
	 ('Sendika sistemimiz internet kesintisi durumunda veya farklı lokasyonlardan erişim gerektiğinde nasıl çalışır?','Sistemimiz %100 bulut tabanlı altyapısı sayesinde internet bağlantısı olan her yerden 7/24 kesintisiz erişim imkanı sunar. Verileriniz güvenli sunucularda yedeklenir ve farklı şubelerden eş zamanlı erişime olanak tanır.',0,true,1,'2025-01-02 15:30:55.3+03','2025-01-02 15:52:25.786+03'),
	 ('Resmi yazışmalar ve belge oluşturma süreçleri nasıl yönetiliyor?','Sistemimizde tüm resmi yazışmalar için hazır şablonlar bulunuyor, sürükle-bırak yöntemiyle belgeleri kolayca oluşturabilirsiniz. Belge formatları ve logolar sistem tarafından otomatik olarak yerleştirilir, sadece içeriği girmeniz yeterlidir.',0,true,2,'2025-01-02 15:34:16.407+03','2025-01-02 15:53:21.487+03'),
	 ('Üye verilerinin güvenliği ve KVKK uyumluluğu nasıl sağlanıyor?','Sistemimiz KVKK ile tam uyumlu olarak tasarlanmış olup, üye verilerinin işlenmesi ve saklanması süreçlerinde gerekli tüm güvenlik önlemleri alınmıştır. Veri saklama politikaları ve yetkilendirme sistemiyle verileriniz güvende tutulur.',0,true,NULL,'2025-01-02 15:54:19.617+03','2025-01-02 15:54:19.617+03'),
	 ('Kamu kurumlarıyla entegrasyon süreçleri nasıl yönetiliyor?','Sistemimiz DETSİS, MERNİS ve KBS gibi kamu sistemleriyle tam entegre çalışır, üye bilgileri otomatik olarak doğrulanır. Resmi işlemler ve bildirimler sistem üzerinden doğrudan ilgili kurumlara iletilebilir.',0,true,NULL,'2025-01-02 15:55:14.734+03','2025-01-02 15:55:14.734+03'),
	 ('Sendika yöneticileri saha çalışmaları sırasında sisteme nasıl erişebilir?','Sistemimiz mobil uyumlu arayüzü sayesinde tablet ve akıllı telefonlardan tam fonksiyonlu olarak erişilebilir. Saha ziyaretleri sırasında üye kayıtları yapılabilir, belgeler anında görüntülenebilir ve düzenlenebilir.',0,true,NULL,'2025-01-02 15:55:42.387+03','2025-01-02 15:55:42.387+03');
